CREATE TABLE parking_bay (street_marker VARCHAR(30) NOT NULL, area VARCHAR(30), street_name VARCHAR(30), between_street_1 VARCHAR(30), between_street_2 VARCHAR(30), PRIMARY KEY (street_marker))
CREATE TABLE parking_event (device_id VARCHAR(10) NOT NULL,arrival_time VARCHAR(50) NOT NULL,departure_time VARCHAR(50),street_marker VARCHAR(30) NOT NULL,parking_sign VARCHAR(30),duration_seconds INTEGER,in_violation BOOLEAN,side_of_street INTEGER CHECK(side_of_street > 0 AND side_of_street <= 5),street_id INTEGER,FOREIGN KEY (street_marker) REFERENCES parking_bay(street_marker),PRIMARY KEY (device_id, arrival_time))
