CREATE TABLE parking_bay (street_marker VARCHAR(50) NOT NULL,area VARCHAR(30),street_name VARCHAR(30),between_street_1 VARCHAR(30),between_street_2 VARCHAR(30),PRIMARY KEY (street_marker))
CREATE TABLE parking_event (device_id INTEGER NOT NULL,arrival_time TIMESTAMP NOT NULL,departure_time TIMESTAMP,duration_seconds BIGINT,street_marker VARCHAR(50) NOT NULL,parking_sign VARCHAR(50),street_id INTEGER,side_of_street INTEGER CHECK(side_of_street > 0 AND side_of_street <= 5),in_violation BOOLEAN,FOREIGN KEY (street_marker) REFERENCES parking_bay(street_marker),PRIMARY KEY (device_id, arrival_time))
